import React, { useState } from 'react';
import { useSelector } from "react-redux";
import axiosFetch from "../../../axios/index";
import "./css.css";

const ModalUser = (props) => {

  const inforUser = useSelector((state) => state.userReducer.dataUser)
  console.log(inforUser);
  const [state, setState] = useState({
    name: inforUser.name,
    email: inforUser.email,
    sdt: inforUser.sdt,
    address: inforUser.address,
  })



  const handleOnchange = (e) => {
    const { name, value } = e.target;
    setState({ [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.props.inforUser);
    console.log(this.state);

    // this.updateUser();
  };

  return (
    <div>
      <div className="ModalUser">
        <div
          className="modal fade"
          id="exampleModal1"
          tabIndex={-1}
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  Update User
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                />
              </div>
              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label
                      htmlFor="recipient-name"
                      className="col-form-label"
                    >
                      Name:
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="recipient-name"
                      name="name"
                      onChange={handleOnchange}
                      value={state.name}
                    />
                  </div>
                  <div className="mb-3">
                    <label
                      htmlFor="recipient-name"
                      className="col-form-label"
                    >
                      email:
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="recipient-name"
                      name="email"
                      value={state.email}
                      onChange={handleOnchange}
                    />
                  </div>
                  <div className="mb-3">
                    <label
                      htmlFor="recipient-name"
                      className="col-form-label"
                    >
                      SDT:
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="recipient-name"
                      name="sdt"
                      value={state.sdt}
                      onChange={handleOnchange}
                    />
                  </div>
                  <div className="mb-3">
                    <label
                      htmlFor="recipient-name"
                      className="col-form-label"
                    >
                      Address:
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="recipient-name"
                      name="address"
                      value={state.address}
                      onChange={handleOnchange}
                    />
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                      id="btn-close2"
                    >
                      Close
                    </button>
                    <button type="submit" className="btn btn-primary">
                      UPDATE
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ModalUser
