import React, { Component } from "react";
import axiosFetch from "../../../axios";
import "./css.css";
import { connect } from "react-redux";

const mapStateToProps = (state) => {
  return {
    inforUser: state.userReducer.dataUser,
  };
};

class ModalUser extends Component {

  constructor(props) {
    super()
    this.state = {
      name: "",
      email: "",
      sdt: "",
      address: "",
    };
    console.log(this.props.inforUser);

  }


  handleOnchange = (e) => {
    const { name, value } = e.target;
    this.props.inforUser.name = value
    this.setState({ [name]: value })
  };
  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.props.inforUser);
    console.log(this.state);

    // this.updateUser();
  };

  updateUser = () => {
    axiosFetch(
      `https://mini-project-crm-api.herokuapp.com/api/v1/users/${this.props.inforUser._id}`,
      "PATCH",
      this.state
    )
      .then((result) => {
        alert("ok roi do ");
        const myModal = document.getElementById("btn-close2");
        myModal.click();
      })
      .catch((err) => {
        console.log(err);
        console.log("loi roi ");
      });
  };

  render() {
    const { name, email, sdt, address } = this.props.inforUser
    return (
      <div>
        <div className="ModalUser">
          <div
            className="modal fade"
            id="exampleModal1"
            tabIndex={-1}
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">
                    Update User
                  </h5>
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  />
                </div>
                <div className="modal-body">
                  <form onSubmit={this.handleSubmit}>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        Name:
                      </label>
                      <input
                        type="text"
                        className="form-control"

                        name="name"
                        onChange={this.handleOnchange}
                        value={name || ""}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        email:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="email"
                        value={email || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        SDT:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="sdt"
                        value={sdt || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        Address:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="address"
                        value={address || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="modal-footer">
                      <button
                        type="button"
                        className="btn btn-secondary"
                        data-bs-dismiss="modal"
                        id="btn-close2"
                      >
                        Close
                      </button>
                      <button type="submit" className="btn btn-primary">
                        UPDATE
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ModalUser);
