import React, { Component } from "react";
import axiosFetch from "../../../axios";
import "./css.css";
import { connect } from "react-redux";

class ModalUser extends Component {

  constructor(props) {
    super(props);
    this.state = {
      user: {
        name: "",
        email: "",
        sdt: "",
        address: "",
      }
    };
  }
  handleOnchange = (e) => {
    let updateUser = { ...this.state.user, [e.target.name]: e.target.value, }
    this.setState({
      user: updateUser
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.updateUser();
  };

  updateUser = () => {
    axiosFetch(
      `https://mini-project-crm-api.herokuapp.com/api/v1/users/${this.props.inforUser._id}`,
      "PATCH",
      this.state.user
    )
      .then((result) => {
        alert("ok roi do ");
        this.props.handleUpdate();
        const myModal = document.getElementById("btn-close2");
        myModal.click();
      })
      .catch((err) => {
        console.log(err);
        console.log("loi roi ");
      });
  };
  render() {
    // const { name, email, sdt, address } = this.state;
    const { name, email, sdt, address } = this.props.inforUser
    return (
      <div>
        <div className="ModalUser">
          <div
            className="modal fade"
            id="exampleModal1"
            tabIndex={-1}
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">
                    Update User
                  </h5>
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  />
                </div>
                <div className="modal-body">
                  <form onSubmit={this.handleSubmit}>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        Name:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="name"
                        onChange={this.handleOnchange}
                        value={name || ""}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        email:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="email"
                        value={email || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        SDT:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="sdt"
                        value={sdt || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="mb-3">
                      <label
                        htmlFor="recipient-name"
                        className="col-form-label"
                      >
                        Address:
                      </label>
                      <input
                        type="text"
                        className="form-control"
                        id="recipient-name"
                        name="address"
                        value={address || ""}
                        onChange={this.handleOnchange}
                      />
                    </div>
                    <div className="modal-footer">
                      <button
                        type="button"
                        className="btn btn-secondary"
                        data-bs-dismiss="modal"
                        id="btn-close2"
                      >
                        Close
                      </button>
                      <button type="submit" className="btn btn-primary">
                        UPDATE
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    inforUser: state.userReducer.dataUser,
  };
};

export default connect(mapStateToProps)(ModalUser);
