import { DELETE_USER_CRM, GET_UPDATE_USER_CRM, GET_USER_CRM } from "./valiable.reducer";

const initialState = {
    dataUserUpdate: {},
    dataUsers: []
}

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_UPDATE_USER_CRM: {
            state.dataUserUpdate = action.datas;
            return { ...state }
        }
        case GET_USER_CRM: {
            state.dataUsers = action.datas;
            return { ...state }
        }
        case DELETE_USER_CRM: {
            let update = [...state.dataUsers];
            let index = update.findIndex(items => items._id === action.id);
            if (index !== -1) {
                update.splice(index, 1);
                state.dataUsers = update
            }
            return { ...state }
        }

        default:
            return state
    }
}
