import React, { Component } from "react";
import axiosFetch from "../../axios";
import "./css.css";
import User from "../User";
import ModalSendMail from "../Modal/SendMailModal";
import { connect } from "react-redux";
import { getDataUser_Action } from "../../reducer/getData.action";


class ListUser extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      DataSearchfi: [],
      searchfi: "",
      stateSearchfi: true,
    };
  }

  componentDidMount() {
    this.fetchGetUser();
  }

  fetchGetUser = async () => {
    await axiosFetch("https://mini-project-crm-api.herokuapp.com/api/v1/users/", "GET", null)
      .then((result) => {
        this.setState({ loading: false });
        this.props.getDataUser(result.data.data.users)
      })
      .catch((err) => {
        console.log(err);
      });
  };


  handleOnchange = (e) => {
    this.setState({ searchfi: e.target.value });
  };

  renderUser = () => {
    return this.props.dataUsers.map((item, index) => {
      return (
        <User key={index} dataItem={item} />
      );
    });
  };

  clickSearch = () => {
    const search = document.getElementById("search_fi");
    if (this.state.stateSearchfi) {
      search.style.width = "150px";
      search.style.display = "inline";
      this.setState({ stateSearchfi: false });
    } else {
      search.style.width = "0px";
      search.style.display = "none";
      this.setState({ stateSearchfi: true });
    }
  };

  render() {
    if (this.state.loading) {
      return "dang loading";
    }
    const { name, sdt, email, address, CurrentSigninAt } = this.props.dataUserById
    console.log(this.props.dataUsers);
    return (
      <div className="containerr">
        <div className="ListUser_container">
          <div className="ListUser_container_menu">
            <h5>Tất Cả Liên Hệ</h5>
            <div className="ListUser_container_menu_icon">
              <input
                type="text"
                onChange={this.handleOnchange}
                className="search_fi"
                id="search_fi"
              />
              <i className="fas fa-search" onClick={this.clickSearch}></i>
              <i className="fas fa-filter"></i>

              <i
                className="fas fa-plus-circle text-primary"
                data-bs-toggle="modal"
                data-bs-target="#exampleModal"
                data-bs-whatever="@mdo"
              ></i>
            </div>
          </div>
          <ul className="ListUser_container_ul">
            {this.renderUser()}
          </ul>
        </div>
        <div className="User_profile">
          <ModalSendMail dataSendMail={email} />
          {!this.props.dataUserById ? <div className="User_profile_container">
            <div className="User_profile_container_content">
              <i
                className="fas fa-share-square "
                data-bs-toggle="modal"
                data-bs-target="#exampleModal2"
                data-bs-whatever="@mdo"
              ></i>
              <div className="User_profile_container_content_avatar">
                <img src="https://static.toiimg.com/thumb/resizemode-4,msid-76729536,width-1200,height-900/76729536.jpg" alt="" />
              </div>
              <div className="User_profile_container_content_text">
                <div
                  className="User_profile_container_content_text_text"
                  id="User_profile_container_content_text_text"
                >
                  <h3>{this.props.dataUsers[0].name}</h3>
                  <p className="text-primary">
                    <i className="fas fa-envelope"></i>
                    {this.props.dataUsers[0].email}
                  </p>
                  <p>
                    <i className="fas fa-phone"></i>0{sdt}
                  </p>
                  <p>
                    <i className="fas fa-home"></i>
                    {this.props.dataUsers[0].address.slice(0, 40)}
                  </p>
                  <p>
                    <i className="fas fa-business-time"></i>
                    {this.props.dataUsers[0].CurrentSigninAt}
                  </p>
                </div>
              </div>
            </div>
          </div>
            : <div className="User_profile_container">
              <div className="User_profile_container_content">
                <i
                  className="fas fa-share-square "
                  data-bs-toggle="modal"
                  data-bs-target="#exampleModal2"
                  data-bs-whatever="@mdo"
                ></i>
                <div className="User_profile_container_content_avatar">
                  <img src="https://static.toiimg.com/thumb/resizemode-4,msid-76729536,width-1200,height-900/76729536.jpg" alt="" />
                </div>
                <div className="User_profile_container_content_text">
                  <div
                    className="User_profile_container_content_text_text"
                    id="User_profile_container_content_text_text"
                  >
                    <h3>{name}</h3>
                    <p className="text-primary">
                      <i className="fas fa-envelope"></i>
                      {email}
                    </p>
                    <p>
                      <i className="fas fa-phone"></i>0{sdt}
                    </p>
                    <p>
                      <i className="fas fa-home"></i>
                      {address ? address.slice(0, 40) : ""}
                    </p>
                    <p>
                      <i className="fas fa-business-time"></i>
                      {CurrentSigninAt}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    );
  }
}

const mapDispacthToProps = (dispacth) => {
  return {
    getDataUser: (data) => {
      dispacth(getDataUser_Action(data))
    }
  }
}

const mapStateToProps = (state) => {
  return {
    dataUsers: state.userReducer.dataUsers,
    dataUserById: state.userReducer.dataUserById
  }
}

export default connect(mapStateToProps, mapDispacthToProps)(ListUser);
