import React, { Component } from "react";
import "./css.css";
import axiosFetch from "../../axios";
import ModalUser from "../Modal/UpdateUser_modal";
import { connect } from "react-redux";
import { getDataUser_Action } from "../../reducer/getData.action";

class User extends Component {

  constructor(props) {
    super()
    this.state = {
      name: "",
      email: "",
      sdt: "",
      address: "",
    };
  }

  clickUser = (data) => {
    this.props.profile(data);
  };

  deleteUser = async (id) => {
    await axiosFetch(
      `https://mini-project-crm-api.herokuapp.com/api/v1/users/${id}`,
      "DELETE",
      null
    )
      .then((result) => {
        this.props.deleteUser();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  setDataUser = (data) => {

    this.setState({
      name: "123",
      email: data.email,
      sdt: data.sdt,
      address: data.address,
    })
    console.log(this.state);
  }

  render() {
    const data = this.props.dataItem;
    return (
      <div>
        <ModalUser dataUsers={this.dataUser} />
        <div
          className="User_container"
          onClick={() => {
            this.clickUser(data);
          }}
        >
          <div className="User_container_content">
            <img src="https://static.toiimg.com/thumb/resizemode-4,msid-76729536,width-1200,height-900/76729536.jpg" alt="" />
            <div>
              <p className="User_name">{data.name}</p>
              <p className="User_email">{data.email}</p>
            </div>
          </div>
          <div className="dropdown">
            <button
              className="btn btn-secondary dropdown-toggle"
              type="button"
              id="dropdownMenuButton1"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              <i className="fas fa-ellipsis-v"></i>
            </button>
            <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
              <li className="dropdown-item"></li>
              <li
                className="dropdown-item"
                data-bs-toggle="modal"
                data-bs-target="#exampleModal1"
                data-bs-whatever="@mdo"
                onClick={() => {
                  this.props.updateUser(data);
                  this.setDataUser(data)
                }}
              >
                Sửa
              </li>
              <li
                className="dropdown-item"
                onClick={() => {
                  this.deleteUser(data._id);
                }}
              >
                Xóa
              </li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

const mapDispacthToProps = (dispacth) => {
  return {
    updateUser: (data) => {
      dispacth(getDataUser_Action(data))
    }
  }
}

export default connect(null, mapDispacthToProps)(User);
